import React, { useState } from 'react'

const Costumer = () => {
   
     const validate = (inputs) =>{
      const errors  = {}
      if(inputs.name.length < 1) return errors.name = 'empty field'
      if(inputs.phoneNumber < 0 ) return errors.phoneNumber = 'empty field'
      if(inputs.email.length < 1) return errors.email = 'empty field'
      if(inputs.hobbies.length === 0) return errors.hobbies = 'empty field'
     }
  const [customers, setCustomers] = useState({
     name:'',
     phoneNumber:0,
     email:'',
     hobbies: [],
  })
  const [errors , setErrors] = useState({
     name:'',
     phoneNumber:0,
     email:'',
     hobbies: [],
  });

  const handleInput = (e) => {
     const {value, name} = e.target
     if (name === "hobbies"){
          setCustomers({ 
               ...customers,
               hobbies: [...customers.hobbies, value]
          })
     }else {
          setCustomers({ 
               ...customers,
               [name]: value
          })
     }
     setErrors(
          validate({
               ...customers,
          [name]: value
          })
     )
  }
  const handleSubmit = (e) => {
     e.preventDefault();
    const allErrors = Object.keys(customers)
    if(allErrors.length === 0) {
     setCustomers({
          name:'',
          phoneNumber:0,
          email:'',
          hobbies: [],
     })
     setErrors({ 
     name:'',
     phoneNumber:0,
     email:'',
     hobbies: [],
     })
    }
  }
  
  return (
    <form onSubmit={handleSubmit}>
     <div>
          <label htmlFor="name">Name</label>
          <input type="text" value={customers.name} name='name' id='name' onChange={handleInput}/> 
          {errors.name && <p>{errors.name}</p>}
     </div>
     <div>
          <label htmlFor="phoneNumber">Phone Number</label>
          <input type="text" value={customers.phoneNumber} name='phoneNumber' id='phoneNumber' onChange={handleInput}/> 
          {errors.phoneNumber && <p>{errors.phoneNumber}</p>}
     </div>
     <div>
          <label htmlFor="email">Email</label>
          <input type="text" value={customers.email} name='email' id='email' onChange={handleInput}/> 
          {errors.email && <p>{errors.email}</p>}
     </div>
     <div>
          <label htmlFor="hobbies">Hobbies</label>
          <input type="text" value={customers.hobbies} name='hobbies' id='hobbies' onChange={handleInput}/> 
          {errors.hobbies && <p>{errors.hobbies}</p>}
     </div>

     <button type="submit">Submit</button>
     
    </form>
  )
}

export default Costumer
